    // Create the Sprite
    let tempBtn = new createjs.Sprite(tempBtnSpriteSheet, "normal");
    tempBtn.mouseEnabled = true;
    tempBtn.mouseChildren = true;
    tempBtn.cursor = "pointer";
    tempBtn.y = canvas.height * deltaY;
    tempBtn.x = isMobile.value ? canvas.width  * .85 :canvas.width  * .82;
    tempBtn.scale = isMobile.value ? .35 * dpr: .4 * dpr; // Apply scaling
    // Add event listeners for hover and click
    tempBtn.on("mouseover", () => {
      tempBtn.gotoAndStop("hover");

    });
    tempBtn.on("mouseout", () => {
      tempBtn.gotoAndStop("normal");
    });
    tempBtn.on("click", () => {
      tempBtn.gotoAndStop("hover");
      setTimeout(() => {
        tempBtn.gotoAndStop("normal");
      }, 200);
      //if there is animation playing,stop the playing animation
      if(playingAnimation){
        playingAnimation.gotoAndPlay("stop");
        stage.removeChild(playingAnimation);
        playingAnimation = null;
      }
      playSound(`sound_${konsonansArr[i].letter}`);
      let animationSpriteSheet = new createjs.SpriteSheet({
        images: [queue.getResult(`${konsonansArr[i].letter}_animation`)],
        framerate: 30,
        frames: konsonansArr[i].frames,
        animations: {
          run: [0,konsonansArr[i].frames.length - 1, "end"],
          end: [konsonansArr[i].frames.length - 1,konsonansArr[i].frames.length - 1],
          stop: [0]
        }
      });
      let animation= new createjs.Sprite(animationSpriteSheet);
      animation.x = canvas.width * (konsonansArr[i].x ?? (isMobile.value ? .2 :.32));
      animation.y = canvas.height * (konsonansArr[i].y ?? (isMobile.value ? .54 :.55)) ; // Adjust this value if needed
      animation.scale = (konsonansArr[i].scale ?? (isMobile.value ? 1.2 :1.4)) * dpr;
      animation.on("animationend", ()=> {handleAnimationEnd(konsonansArr[i])});

      hideDisplayKonsonanButtons();
      hideDisplayImageText();
      stage.addChild(animation);
      animation.gotoAndPlay("run");
      playingAnimation = animation;

      stage.addChild(btnSubmenu);//required because the button will be covered the letter
    });
    stage.addChild(tempBtn);
    displayedKonsonanButtons.push(tempBtn);
    deltaY += isMobile.value ? .12 :.14;
  }
}
function handleAnimationEnd(konsonan){
  let deltaY = isMobile.value ? .43 : 0.4 ;
  let words = konsonan.words;
  for(let i = 0; i < words.length; i++){
    let btnSpriteSheet = new createjs.SpriteSheet({
      images: [
        queue.getResult(`btn_${words[i].word}_01`), // Normal state
        queue.getResult(`btn_${words[i].word}_02`)  // Hover / Click state
      ],
      frames: { width: words[i].width, height: words[i].height}, // Adjust size as needed
      animations: {
        normal: 0,
        hover: 1
      }
    });
    // Create the Sprite
    let btnKonsonanImage = new createjs.Sprite(btnSpriteSheet, "normal");
    btnKonsonanImage.mouseEnabled = true;
    btnKonsonanImage.mouseChildren = true;
    btnKonsonanImage.cursor = "pointer";
    btnKonsonanImage.y = canvas.height * deltaY;
    btnKonsonanImage.x = isMobile.value ? canvas.width * .05  :  canvas.width * .2;
    btnKonsonanImage.scale =  isMobile.value ? .25 * dpr : .3 * dpr; // Apply scaling
    // Add event listeners for hover and click
    btnKonsonanImage.on("mouseover", () => {
      btnKonsonanImage.gotoAndStop("hover");
    });
    btnKonsonanImage.on("mouseout", () => {
      btnKonsonanImage.gotoAndStop("normal");
    });
    btnKonsonanImage.on("click", () => {
      btnKonsonanImage.gotoAndStop("hover");
      setTimeout(() => {
        btnKonsonanImage.gotoAndStop("normal");
      }, 200);
      displayImageTextOnTv(words[i].word);
      playSound(`sound_${words[i].word}`);
    });
    stage.addChild(btnKonsonanImage);
    displayedKonsonanImageButtons.push(btnKonsonanImage);
    deltaY += (isMobile.value ? .08 :  .11);
  }
}
function displayImageTextOnTv(word){
  /*
  * display text and image related to the word clicked on tv screen
  * */
  hideDisplayImageText();

  displayedImage  = new createjs.Bitmap(queue.getResult(`btn_${word}_01`));
  displayedImage.regX = displayedImage.image.width / 2;
  displayedImage.regY = displayedImage.image.height / 2;
  displayedImage.x = isMobile.value ?  canvas.width * .45: canvas.width * .53;
  displayedImage.y = canvas.height * .55 ;
  displayedImage.alpha = 0; // Initially hidden
  displayedImage.scale = 1 * dpr;
  stage.addChild(displayedImage);

  displayedText  = new createjs.Bitmap(queue.getResult(`text_${word}`));
  displayedText.regX = displayedText.image.width / 2;
  displayedText.regY = displayedText.image.height / 2;
  displayedText.x = isMobile.value ? canvas.width * .45: canvas.width * .55;
  displayedText.y =  isMobile.value ?  canvas.height * .73 :canvas.height * .8;
  displayedText.alpha = 0; // Initially hidden
  displayedText.scale =  isMobile.value ?  .3 * dpr : .35 * dpr;
  stage.addChild(displayedText);

  // Apply fade-in animation (0 to 1 alpha in 1 second)
  createjs.Tween.get(displayedImage).to({ alpha: 1 }, 500, createjs.Ease.quadIn);
  createjs.Tween.get(displayedText).to({ alpha: 1 }, 500, createjs.Ease.quadIn);
}
function hideDisplayKonsonanButtons(hideLetter = false){
  /*
  * remove any image buttons at the side of the tv and optionally the konsonan buttons
  * */
  if(displayedKonsonanImageButtons.length > 0){
    displayedKonsonanImageButtons.forEach((btn)=>{
      stage.removeChild(btn);
    });
  }
  if(hideLetter){
    displayedKonsonanButtons.forEach((btn)=>{
      stage.removeChild(btn);
    });
  }
}
function hideDisplayImageText(){
  /*
  * remove any image buttons on the tv
  * */
  if(displayedImage){
    stage.removeChild(displayedImage);
    displayedImage = null;
  }
  if(displayedText){
    stage.removeChild(displayedText);
    displayedText= null;
  }

}
function navigateToAnotherPage(direction){
  /*
  * to handle page navigation when clicking on next or previous button
  * */
  if(playingAnimation){
    playingAnimation.gotoAndPlay("stop");
    stage.removeChild(playingAnimation);
    playingAnimation = null;
  }
  if(direction === "next"){
    if(currentPage>= 1 && currentPage <  konsonans.length){
      hideDisplayKonsonanButtons(true);
      hideDisplayImageText();
      currentPage++;
      initButtons();//initialize new konsonan button

    }
    else if(currentPage === konsonans.length){
      navigateToAnotherSubmenu();
    }
  }else if(direction === "previous"){
    if(currentPage <=  konsonans.length && currentPage > 1){
      hideDisplayKonsonanButtons(true);
      hideDisplayImageText();
      currentPage--;
      initButtons();//initialize new konsonan button

    }
    else if(currentPage === 1){
      router.push({name: 'AbjadVokalBergambar'});
    }
  }
}
function playSound(sound){
  if (playingSound) {
    playingSound.stop();
    playingSound = null;
  }
  playingSound = createjs.Sound.play(sound);
}
function cleanupCreateJS() {
  if (queue) {
    queue.removeAllEventListeners(); // Remove all event listeners
    queue.removeAll(); // Clear all loaded assets
    queue = null;
  }

  if (stage) {
    createjs.Ticker.removeEventListener("tick", handleTick); // Stop ticker updates
    stage.removeAllChildren(); // Remove all stage objects
    stage.clear(); // Force clear rendering
    stage = null;
  }

  if (playingSound) {
    playingSound.stop();
    playingSound = null;
  }

  // Manually destroy all CreateJS objects
  createjs.Sound.removeAllSounds();
  createjs.Sound.stop();
}
function navigateToAnotherSubmenu(){
  playSound("sound_ingin_teruskan");
  showModalContinue.value = true;
}
// Lifecycle Hook
onMounted(() => {
  init();
});
onUnmounted(() => {
  cleanupCreateJS(); // Clean up before leaving the page
});
</script>

<template>
  <div id="container">
    <canvas id="canvas"></canvas>
  </div>

  <Transition name="fade-in">
    <ModalContinue @hide="showModalContinue = false;" v-if="showModalContinue"></ModalContinue>
  </Transition>

  <Loader v-if="isLoading"></Loader>
</template>

